import React, { useState, useEffect } from "react";
import axios from "axios";
import Header from "./components/Header";
import SearchBar from "./components/SearchBar";
import LocationList from "./components/LocationList";
import AddLocationForm from "./components/AddLocationForm";
import "./App.css";

function App() {
    const [locations, setLocations] = useState([]);

    useEffect(() => {
        axios.get("/api/locations").then((response) => {
            setLocations(response.data);
        });
    }, []);
    useEffect(() => {
        fetch("/api/locations")
            .then((response) => response.json())  // Parse the JSON data from the response
            .then((data) => {
                setLocations(data);  // Update the state with the fetched data
            })
            .catch((error) => {
                console.error("Error fetching locations:", error);  // Handle errors
            });
    }, []);


    const addLocation = (location) => {
        axios.post("/api/add_location", location).then((response) => {
            setLocations([...locations, response.data]);
        });
    };

    return (
        <div className="app">
            <Header />
            <SearchBar />
            <LocationList locations={locations} />
            <AddLocationForm addLocation={addLocation} />
        </div>
    );
}

export default App;
