import React, { useState, useEffect } from "react";
import axios from "axios";
import Header from "./components/Header";
import SearchBar from "./components/SearchBar";
import LocationList from "./components/LocationList";
import AddLocationForm from "./components/AddLocationForm";
import "./App.css";

function App() {
    const [locations, setLocations] = useState([]);
    const [filteredLocations, setFilteredLocations] = useState([]);
    const [searchQuery, setSearchQuery] = useState("")
    const [suggestions, setSuggestions] = useState([]); // Store fetched location suggestions

    // Fetch location suggestions based on user input
    const fetchLocationSuggestions = (query) => {
        if (query.length < 3) { // Only make the request if the query is at least 3 characters long
            setSuggestions([]); // Clear suggestions if query is too short
            return;
        }

        axios
            .get(`http://127.0.0.1:5000/locations?query=${query}`)
            .then((response) => {
                setSuggestions(response.data); // Update suggestions with response from API
            })
            .catch((error) => {
                console.error("Error fetching location suggestions:", error);
            });
    };
    const getLocations = () => {
        fetch("http://127.0.0.1:5000/locations")
            .then((response) => response.json())  // Parse the JSON data from the response
            .then((data) => {
                setLocations(data);  // Update the state with the fetched data
            })
            .catch((error) => {
                console.error("Error fetching locations:", error);  // Handle errors
            });

    };
    const handleSearchChange = (event) => {
        const query = event.target.value;
        setSearchQuery(query);
        fetchLocationSuggestions(query); // Fetch suggestions whenever user types
    };
    const handleLocationSelect = (location) => {
        setSearchQuery(location); // Set the input field to the selected location
        setSuggestions([]); // Clear the suggestions list after selection
    };

    const addLocation = (location) => {
        fetch("http://127.0.0.1:5000/add_location", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",  // Specify the content type
            },
            body: JSON.stringify(location),  // Convert the location data to a JSON string
        })
            .then((response) => response.json())  // Parse the JSON response
            .then((data) => {
                setLocations((prevLocations) => [...prevLocations, data]);  // Update the state with the new location
            })
            .catch((error) => {
                console.error("Error adding location:", error);  // Handle errors
            });

    };

    return (
        <div className="app">
            <Header />
            <div className="search-bar">
                <input type="text" placeholder="Enter location..." value={searchQuery}
                    onChange={handleSearchChange} />
                <button onClick={getLocations}>Search</button>
                {/* {suggestions.length > 0 && searchQuery && (
                    <ul className="suggestions"> */}
                {suggestions.map((location, index) => (
                    <li key={index} onClick={() => handleLocationSelect(location)}>
                        {location} {/* Show each suggestion */}
                    </li>
                ))}
                {/* </ul>
                )} */}
            </div>
            <LocationList locations={locations} />
            <AddLocationForm addLocation={addLocation} />
        </div>
    );
}

export default App;
